mapper.select:
        SELECT


--         级联表的字段
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.new_money as newMoney
        ,yonghu.yonghu_email as yonghuEmail

        FROM sucai  a
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and a.sucai_uuid_number like CONCAT('%',#{params.sucaiUuidNumber},'%')


                and a.sucai_name like CONCAT('%',#{params.sucaiName},'%')


                and a.sucai_types = #{params.sucaiTypes}


                and a.sucai_erji_types = #{params.sucaiErjiTypes}


                and a.sucai_video like CONCAT('%',#{params.sucaiVideo},'%')


                and a.sucai_music like CONCAT('%',#{params.sucaiMusic},'%')


                  and a.sucai_old_money >= #{params.sucaiOldMoneyStart}   


                  and a.sucai_old_money <= #{params.sucaiOldMoneyEnd}   


                  and a.sucai_new_money >= #{params.sucaiNewMoneyStart}   


                  and a.sucai_new_money <= #{params.sucaiNewMoneyEnd}   


                  and a.sucai_clicknum >= #{params.sucaiClicknumStart}   


                  and a.sucai_clicknum <= #{params.sucaiClicknumEnd}   


                and a.sucai_clicknum = #{params.sucaiClicknum}


                and a.sucai_content like CONCAT('%',#{params.sucaiContent},'%')


                and a.shangxia_types = #{params.shangxiaTypes}


                  and a.sucai_delete >= #{params.sucaiDeleteStart}   


                  and a.sucai_delete <= #{params.sucaiDeleteEnd}   


                and a.sucai_delete = #{params.sucaiDelete}




                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')



        order by a.${params.orderBy} desc
